import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterProduct = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [product, setProduct] = useState({
        id: '',
        name: '',
        price: '',
        brand: '', // Marca como campo de texto
        categorieName: '', // ID de la categoría seleccionada
        thumbImage: null, // Imagen miniatura
        images: [] // Imágenes normales
    });

    const categoriesApiUrl = "http://0.0.0.0:8090/api/collections/categories/records";
    const productsApiUrl = "http://0.0.0.0:8090/api/collections/products/records";

    useEffect(() => {
        const fetchCategories = async () => {
            try {
                const response = await axios.get(categoriesApiUrl);
                setCategories(response.data.items);
            } catch (error) {
                console.error("Error al cargar las categorías:", error);
            }
        };

        fetchCategories();
    }, []);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setProduct(prevState => ({
            ...prevState,
            [id]: value
        }));
    };

    const handleFileChange = (e, type) => {
        if (type === 'thumbImage') {
            setProduct({ ...product, thumbImage: e.target.files[0] });
        } else if (type === 'images') {
            setProduct({ ...product, images: [...e.target.files] });
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('name', product.name);
        formData.append('price', product.price);
        formData.append('brand', product.brand);
        formData.append('categorieName', product.categorieName);

        if (product.thumbImage) {
            formData.append('thumbImage', product.thumbImage);
        }

        if (product.images && product.images.length) {
            product.images.forEach((file, index) => {
                formData.append(`images_${index}`, file);
            });
        }

        try {
            if (product.id) {
                await axios.patch(`${productsApiUrl}/${product.id}`, formData);
            } else {
                await axios.post(productsApiUrl, formData);
            }
            setProduct({ id: '', name: '', price: '', brand: '', categorieName: '', thumbImage: null, images: [] });
        } catch (error) {
            console.error("Error al procesar el producto:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE PRODUCTOS" current />
            </Breadcrumb>

            <style>
                {`
                    .form-container {
                        width: 360px;
                        margin: auto;
                        padding: 20px;
                        background-color: #f9f9f9;
                        border-radius: 8px;
                        box-shadow: 0 0 10px rgba(0,0,0,0.1);
                    }

                    .form-input {
                        font-family: "Roboto", sans-serif;
                        outline: 0;
                        background: #f2f2f2;
                        width: 100%;
                        border: 0;
                        margin: 0 0 15px;
                        padding: 15px;
                        box-sizing: border-box;
                        font-size: 14px;
                    }

                    .form-button {
                        font-family: "Roboto", sans-serif;
                        text-transform: uppercase;
                        outline: 0;
                        background: #f26460;
                        width: 100%;
                        border: 0;
                        padding: 15px;
                        color: #FFFFFF;
                        font-size: 14px;
                        cursor: pointer;
                        transition: all 0.3s ease;
                    }

                    .form-button:hover {
                        background: #ee3631;
                    }

                    .data-table {
                        width: 100%;
                        margin-top: 20px;
                        border-collapse: collapse;
                    }

                    .data-table th, .data-table td {
                        text-align: left;
                        padding: 8px;
                    }

                    .data-table thead {
                        background-color: #f26460;
                        color: white;
                    }

                    .data-table tbody tr:nth-child(odd) {
                        background-color: #f9f9f9;
                    }

                    .table-button {
                        background: #f26460;
                        color: #FFFFFF;
                        border: none;
                        padding: 5px 10px;
                        border-radius: 5px;
                        cursor: pointer;
                    }

                    .table-button:hover {
                        background: #ee3631;
                    }
                `}
            </style>

            <div className="form-container">
                {/* ... */}
                <form className="login-form" onSubmit={handleSubmit}>
                    <div className="form-group">
                        <label htmlFor="productName">Nombre del Producto</label>
                        <input
                            type="text"
                            className="form-input"
                            id="productName"
                            name="name"  // Asegúrate de que el atributo 'name' coincida con la clave del estado de 'product'
                            placeholder="Ingrese el nombre del producto"
                            value={product.name}
                            onChange={handleInputChange}
                        />
                    </div>
                    <div className="form-group">
                <label htmlFor="productPrice">Precio</label>
                <input 
                    type="number" 
                    className="form-input" 
                    id="productPrice" 
                    name="price"  // Asegúrate de que el atributo 'name' sea 'price'
                    placeholder="Ingrese el precio" 
                    value={product.price} 
                    onChange={handleInputChange} 
                />
            </div>

                    <div className="form-group">
                        <label htmlFor="productBrand">Marca</label>
                        <input 
                            type="text" 
                            className="form-input" 
                            id="productBrand" 
                            placeholder="Ingrese la marca" 
                            value={product.brand} 
                            onChange={handleInputChange} 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="categorieName">Categoría</label>
                        <select 
                            className="form-input" 
                            id="categorieName" 
                            value={product.categorieName} 
                            onChange={handleInputChange}
                        >
                            <option value="">Seleccione una categoría</option>
                            {categories.map(category => (
                                <option key={category.id} value={category.id}>{category.categorieName}</option>
                            ))}
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="thumbImage">Imagen Miniatura</label>
                        <input 
                            type="file" 
                            className="form-input" 
                            id="thumbImage" 
                            onChange={(e) => handleFileChange(e, 'thumbImage')} 
                        />
                    </div>
                    <div className="form-group">
                        <label htmlFor="images">Imágenes</label>
                        <input 
                            type="file" 
                            className="form-input" 
                            id="images" 
                            multiple 
                            onChange={(e) => handleFileChange(e, 'images')} 
                        />
                    </div>
                    <button type="submit" className="form-button">Enviar</button>
                </form>
            </div>
        </LayoutFour>
    );
};

export default RegisterProduct;
