import React, { useState } from 'react';
import $ from 'jquery'; 
import LayoutFour from "../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../components/Other/Breadcrumb";

const LoginPage = () => {
  const [isRegistering, setIsRegistering] = useState(false);

  const toggleForm = () => {
    setIsRegistering(!isRegistering);
  };

  $('.message a').click(function(){
    $('form').animate({height: "toggle", opacity: "toggle"}, "slow");
 });

  return (
    <LayoutFour title="INICIO" className="-style-1">
        <Breadcrumb title="ADMINISTRACIÓN">
        <BreadcrumbItem name="INICIO" />
        <BreadcrumbItem name="ADMINISTRACIÓN" current />
      </Breadcrumb>
      <style>
        {`
          @import url(https://fonts.googleapis.com/css?family=Roboto:300);

          ._login-page {
            width: 360px;
            padding: 8% 0 0;
            margin: auto;
          }

          ._form input {
            font-family: "Roboto", sans-serif;
            outline: 0;
            background: #f2f2f2;
            width: 100%;
            border: 0;
            margin: 0 0 15px;
            padding: 15px;
            box-sizing: border-box;
            font-size: 14px;
          }

          ._form button {
            font-family: "Roboto", sans-serif;
            text-transform: uppercase;
            outline: 0;
            background: #f26460;
            width: 100%;
            border: 0;
            padding: 15px;
            color: #FFFFFF;
            font-size: 14px;
            -webkit-transition: all 0.3 ease;
            transition: all 0.3 ease;
            cursor: pointer;
          }

          ._form button:hover, ._form button:active, ._form button:focus {
            background: #ee3631;
          }

          ._form ._message {
            margin: 15px 0 0;
            color: #b3b3b3;
            font-size: 12px;
          }

          ._form ._message a {
            color: #f26460;
            text-decoration: none;
          }

          ._form ._register-form {
            display: none;
          }

          ._container ._info span {
            color: #4d4d4d;
            font-size: 12px;
          }

          ._container ._info span a {
            color: #000000;
            text-decoration: none;
          }

          ._container ._info span ._fa {
            color: #EF3B3A;
          }

          body {
            background: #FFFFFF; /* fallback for old browsers */
            font-family: "Roboto", sans-serif;
            -webkit-font-smoothing: antialiased;
            -moz-osx-font-smoothing: grayscale;      
          }
        `}
      </style>

      <div className="_login-page">
        <div className="_form">
          {isRegistering ? (
            <form className="_register-form">
              <input type="text" placeholder="name" />
              <input type="password" placeholder="password" />
              <input type="text" placeholder="email address" />
              <button type="submit">create</button>
              <p className="_message">Already registered? <span onClick={toggleForm}>Sign In</span></p>
            </form>
          ) : (
            <form className="_login-form">
              <input type="text" placeholder="username" />
              <input type="password" placeholder="password" />
              <button type="submit">login</button>
              <p className="_message">Not registered? <span onClick={toggleForm}>Create an account</span></p>
            </form>
          )}
        </div>
      </div>
    </LayoutFour>
  );
};

export default LoginPage;
