import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterProduct = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [product, setProduct] = useState({
        id: '',
        name: '',
        price: '',
        brand: '',
        categorieName: '',
        thumbImage: null,
        images: []
    });

    const categoriesApiUrl = "http://0.0.0.0:8090/api/collections/categories/records";
    const productsApiUrl = "http://0.0.0.0:8090/api/collections/products/records";

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [categoriesResponse, productsResponse] = await Promise.all([
                    axios.get(categoriesApiUrl),
                    axios.get(productsApiUrl)
                ]);
                setCategories(categoriesResponse.data.items);
                setProducts(productsResponse.data.items);
            } catch (error) {
                console.error("Error al cargar datos:", error);
            }
        };

        fetchData();
    }, []);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setProduct(prevState => ({
            ...prevState,
            [id]: value
        }));
    };

    const handleFileChange = (e, type) => {
        if (type === 'thumbImage') {
            setProduct({ ...product, thumbImage: e.target.files[0] });
        } else if (type === 'images') {
            setProduct({ ...product, images: [...e.target.files] });
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('name', product.name);
        formData.append('price', product.price);
        formData.append('brand', product.brand);
        formData.append('categorieName', product.categorieName);
        if (product.thumbImage) {
            formData.append('thumbImage', product.thumbImage);
        }
        if (product.images && product.images.length) {
            product.images.forEach((file, index) => {
                formData.append(`images_${index}`, file);
            });
        }

        try {
            if (product.id) {
                await axios.patch(`${productsApiUrl}/${product.id}`, formData);
            } else {
                await axios.post(productsApiUrl, formData);
            }
            fetchData();
            setProduct({ id: '', name: '', price: '', brand: '', categorieName: '', thumbImage: null, images: [] });
        } catch (error) {
            console.error("Error al procesar el producto:", error);
        }
    };

    const handleUpdateClick = (productToUpdate) => {
        setProduct(productToUpdate);
    };

    const handleDeleteClick = async (productId) => {
        try {
            await axios.delete(`${productsApiUrl}/${productId}`);
            fetchData();
        } catch (error) {
            console.error("Error al eliminar el producto:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE PRODUCTOS" current />
            </Breadcrumb>

            <style>
            {`
                @import url('https://fonts.googleapis.com/css?family=Roboto:300');
                .form-container {
                    width: 360px;
                    padding: 20px;
                    margin: 40px auto;
                    background-color: #f9f9f9;
                    border-radius: 8px;
                    box-shadow: 0 0 10px rgba(0,0,0,0.1);
                    transition: all 0.3s ease;
                }

                .form-input, .form-select, .form-control-file {
                    font-family: "Roboto", sans-serif;
                    outline: 0;
                    background: #f2f2f2;
                    width: 100%;
                    border: 0;
                    margin: 0 0 15px;
                    padding: 15px;
                    box-sizing: border-box;
                    font-size: 14px;
                }

                .form-button {
                    font-family: "Roboto", sans-serif;
                    text-transform: uppercase;
                    outline: 0;
                    background: #f26460;
                    width: 100%;
                    border: 0;
                    padding: 15px;
                    color: #FFFFFF;
                    font-size: 14px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    font-weight: bold;
                }

                .form-button:hover {
                    background: #ee3631;
                }

                /* Estilos para la Tabla */
                .data-table {
                    width: 100%;
                    margin-top: 20px;
                    border-collapse: collapse;
                }

                .data-table th, .data-table td {
                    text-align: left;
                    padding: 8px;
                    border-bottom: 1px solid #ddd;
                }

                .data-table th {
                    background-color: #f26460;
                    color: white;
                }

                .data-table tbody tr:nth-child(odd) {
                    background-color: #f9f9f9;
                }

                .table-button {
                    background: #f26460;
                    color: #FFFFFF;
                    border: none;
                    padding: 5px 10px;
                    border-radius: 5px;
                    cursor: pointer;
                    margin-right: 5px;
                }

                .table-button:hover {
                    background: #ee3631;
                }

                .containerAll {
                    padding-bottom: 70px;
                }
            `}
            </style>

            <div className="containerAll">
                <div className="form-container">
                    <h2>Formulario de Productos</h2>
                    <form onSubmit={handleSubmit}>
                        {/* Resto del código del formulario */}
                    </form>
                </div>

                {/* Tabla de Productos */}
                <table className="data-table">
                    {/* Resto del código de la tabla */}
                </table>
            </div>
        </LayoutFour>
    );
};

export default RegisterProduct;
