import React, { useState } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterService= () => {
    const [service, setService] = useState({
        serviceName: '',
        videoSrc: '',
        description: '',
        serviceImage: null
    });

    const handleInputChange = (e) => {
        setService({ ...service, [e.target.id]: e.target.value });
    };

    const handleFileChange = (e) => {
        setService({ ...service, serviceImage: e.target.files[0] });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('serviceName', service.serviceName);
        formData.append('videoSrc', service.videoSrc);
        formData.append('description', service.description);

        if (service.serviceImage) {
            formData.append('serviceImage', service.serviceImage);
        }

        try {
            const response = await axios.post('http://127.0.0.1:8090/api/your-service-endpoint', formData);
            console.log('Service submitted:', response.data);
            // Limpiar el formulario o redirigir según necesidad
        } catch (error) {
            console.error("Error al enviar el servicio:", error);
        }
    };

    return(
        <div className="login-page">
    <h2>Formulario de Servicios</h2>
    <div className="form">
        <form className="login-form" onSubmit={handleSubmit}>
            <div className="form-group">
                <label htmlFor="serviceName">Nombre del Servicio</label>
                <input type="text" id="serviceName" placeholder="Ingrese el nombre del servicio" onChange={handleInputChange} />
            </div>
            <div className="form-group">
                <label htmlFor="videoSrc">videoSrc</label>
                <input type="text" id="videoSrc" placeholder="URL del video" onChange={handleInputChange} />
            </div>
            <div className="form-group">
                <label htmlFor="description">Descripcion</label>
                <input type="text" id="description" placeholder="Descripción del servicio" onChange={handleInputChange} />
            </div>
            <div className="form-group">
                <label htmlFor="serviceImage">Imagen del Servicio 300x300</label>
                <input type="file" id="serviceImage" className="form-control-file" onChange={handleFileChange} />
            </div>

            <button type="submit" className="bold-text">Enviar</button>
        </form>
    </div>
</div>
    );
};

export default RegisterService;
