import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterProduct = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [product, setProduct] = useState({
        id: '',
        name: '',
        price: '',
        brand: '',
        image: null
    });

    const categoriesApiUrl = "http://0.0.0.0:8090/api/collections/categories/records";
    const productsApiUrl = "http://0.0.0.0:8090/api/collections/products/records";

    const fetchCategories = async () => {
        try {
            const response = await axios.get(categoriesApiUrl);
            setCategories(response.data.items);
        } catch (error) {
            console.error("Error al cargar las categorÃ­as:", error);
        }
    };

    const fetchProducts = async () => {
        try {
            const response = await axios.get(productsApiUrl);
            setProducts(response.data.items);
        } catch (error) {
            console.error("Error al cargar los productos:", error);
        }
    };

    useEffect(() => {
        fetchCategories();
        fetchProducts();
    }, []);

    const handleInputChange = (e) => {
        setProduct({ ...product, [e.target.id]: e.target.value });
    };

    const handleFileChange = (e) => {
        setProduct({ ...product, image: e.target.files[0] });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('name', product.name);
        formData.append('price', product.price);
        formData.append('brand', product.brand);
        if (product.image) {
            formData.append('image', product.image);
        }

        try {
            if (product.id) {
                await axios.patch(`${productsApiUrl}/${product.id}`, formData);
            } else {
                await axios.post(productsApiUrl, formData);
            }
            fetchProducts();
            setProduct({ id: '', name: '', price: '', brand: '', image: null });
        } catch (error) {
            console.error("Error al procesar el producto:", error);
        }
    };

    const handleUpdateClick = (productToUpdate) => {
        setProduct({ ...productToUpdate });
    };

    const handleDeleteClick = async (productId) => {
        try {
            await axios.delete(`${productsApiUrl}/${productId}`);
            fetchProducts();
        } catch (error) {
            console.error("Error al eliminar el producto:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÃN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÃN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE PRODUCTOS" current />
            </Breadcrumb>

            <style>
                {

                }
            </style>

            <div className="login-page">
                <h2>Formulario de Productos</h2>
                <div className="form">
                    {/* Formulario */}
                    {/* ... */}
                </div>
                {/* Tabla */}
                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Producto</th>
                            <th>Precio</th>
                            <th>Marca</th>
                            <th>Actualizar</th>
                            <th>Eliminar</th>
                        </tr>
                    </thead>
                    <tbody>
                        {products.map((product) => (
                            <tr key={product.id}>
                                <td>{product.name}</td>
                                <td>{product.price}</td>
                                <td>{product.brand}</td>
                                <td>
                                    <button className="table-button" onClick={() => handleUpdateClick(product)}>Actualizar</button>
                                </td>
                                <td>
                                    <button className="table-button" onClick={() => handleDeleteClick(product.id)}>Eliminar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </LayoutFour>
    );
};

export default RegisterProduct;
