import React, { useState, useEffect } from 'react';
import axios from 'axios';

const RegisterCategory = () => {
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState({ id: '', categorieName: '' });
    const apiURL = "http://0.0.0.0:8090/api/collections/categories/records";

    const fetchCategories = async () => {
        try {
            const response = await axios.get(apiURL);
            setCategories(response.data.items);
        } catch (error) {
            console.error("Error al cargar las categorías:", error);
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const data = { categorieName: selectedCategory.categorieName };
            if (selectedCategory.id) {
                await axios.put(`${apiURL}/${selectedCategory.id}`, data);
            } else {
                await axios.post(apiURL, data);
            }
            fetchCategories();
            setSelectedCategory({ id: '', categorieName: '' });
        } catch (error) {
            console.error("Error al guardar la categoría:", error);
        }
    };

    const handleUpdateClick = (category) => {
        setSelectedCategory(category);
    };

    const handleDeleteClick = async (categoryId) => {
        try {
            await axios.delete(`${apiURL}/${categoryId}`);
            fetchCategories();
        } catch (error) {
            console.error("Error al eliminar la categoría:", error);
        }
    };

<style>
                {`
                    .login-page {
                        width: 360px;
                        margin: auto;
                        padding-bottom: 60px;
                    }

                    .login-form input {
                        font-family: "Roboto", sans-serif;
                        outline: 0;
                        background: #f2f2f2;
                        width: 100%;
                        border: 0;
                        margin: 0 0 15px;
                        padding: 15px;
                        box-sizing: border-box;
                        font-size: 14px;
                    }

                    .login-form button {
                        font-family: "Roboto", sans-serif;
                        text-transform: uppercase;
                        outline: 0;
                        background: #f26460;
                        width: 100%;
                        border: 0;
                        padding: 15px;
                        color: #FFFFFF;
                        font-size: 14px;
                        -webkit-transition: all 0.3 ease;
                        transition: all 0.3 ease;
                        cursor: pointer;
                    }

                    .login-form button:hover,.login-form button:active,.login-form button:focus {
                        background: #ee3631;
                    }

                    .login-form label {
                        font-family: "Roboto", sans-serif;
                        font-size: 14px;
                        color: #333;
                        margin-bottom: 5px;
                        display: block;
                    }
                `}
</style>

    return (

        <div>
            {/* Formulario */}
            <form onSubmit={handleSubmit}>
                <input type="hidden" value={selectedCategory.id} />
                <div>
                    <label htmlFor="categorieName">Nombre de la Categoría</label>
                    <input type="text" id="categorieName" value={selectedCategory.categorieName} onChange={(e) => setSelectedCategory({ ...selectedCategory, categorieName: e.target.value })} placeholder="Ingrese el nombre de la categoría" />
                </div>
                <button type="submit">Enviar</button>
            </form>

            {/* Tabla */}
            <table>
                <thead>
                    <tr>
                        <th>Categoría</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {categories.map(category => (
                        <tr key={category.id}>
                            <td>{category.categorieName}</td>
                            <td>
                                <button onClick={() => handleUpdateClick(category)}>Actualizar</button>
                                <button onClick={() => handleDeleteClick(category.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
        </div>
    );
};

export default RegisterCategory;
