import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterProduct = () => {
    const [categories, setCategories] = useState([]);
    const [product, setProduct] = useState({
        name: '',
        price: '',
        brand: '',
        image: null
    });

    const categoriesApiUrl = "http://0.0.0.0:8090/api/collections/categories/records";
    const productsApiUrl = "http://0.0.0.0:8090/api/collections/products/records";

    const fetchCategories = async () => {
        try {
            const response = await axios.get(categoriesApiUrl);
            setCategories(response.data.items);
        } catch (error) {
            console.error("Error al cargar las categorías:", error);
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const handleInputChange = (e) => {
        setProduct({ ...product, [e.target.id]: e.target.value });
    };

    const handleFileChange = (e) => {
        setProduct({ ...product, image: e.target.files[0] });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('name', product.name);
        formData.append('price', product.price);
        formData.append('brand', product.brand);
        if (product.image) {
            formData.append('image', product.image);
        }

        try {
            await axios.post(productsApiUrl, formData);
            // Resetear formulario o manejar el estado según sea necesario
        } catch (error) {
            console.error("Error al registrar el producto:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE PRODUCTOS" current />
            </Breadcrumb>

            <style>
                {`
                    /* Estilos para el Formulario */
                    .form-container {
                        width: 360px;
                        margin: auto;
                        padding: 20px;
                        background-color: #f9f9f9;
                        border-radius: 8px;
                        box-shadow: 0 0 10px rgba(0,0,0,0.1);
                    }

                    .form-input, .form-select {
                        font-family: "Roboto", sans-serif;
                        outline: 0;
                        background: #f2f2f2;
                        width: 100%;
                        border: 0;
                        margin-bottom: 15px;
                        padding: 15px;
                        box-sizing: border-box;
                        font-size: 14px;
                    }

                    .form-button {
                        font-family: "Roboto", sans-serif;
                        text-transform: uppercase;
                        outline: 0;
                        background: #f26460;
                        width: 100%;
                        border: 0;
                        padding: 15px;
                        color: #FFFFFF;
                        font-size: 14px;
                        transition: all 0.3 ease;
                        cursor: pointer;
                    }

                    .form-button:hover {
                        background: #ee3631;
                    }

                    /* Estilos para la Tabla */
                    .data-table {
                        width: 100%;
                        margin-top: 20px;
                        border-collapse: collapse;
                    }

                    .data-table th, .data-table td {
                        text-align: left;
                        padding: 8px;
                    }

                    .data-table thead {
                        background-color: #f26460;
                        color: white;
                    }

                    .data-table tbody tr:nth-child(odd) {
                        background-color: #f9f9f9;
                    }

                    .table-button {
                        background: #f26460;
                        color: #FFFFFF;
                        border: none;
                        padding: 5px 10px;
                        margin-right: 10px;
                        border-radius: 5px;
                        cursor: pointer;
                    }

                    .table-button:hover {
                        background: #ee3631;
                    }

                `}
</style>

            <div className="login-page">
                <h2>Formulario de Productos</h2>
                <div className="form">
                    <form className="login-form" onSubmit={handleSubmit}>
                        <div className="form-group">
                            <label htmlFor="productName">Nombre del Producto</label>
                            <input type="text" id="productName" placeholder="Ingrese el nombre del producto" value={product.name} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="productPrice">Precio</label>
                            <input type="number" id="productPrice" placeholder="Ingrese el precio" value={product.price} onChange={handleInputChange} />
                        </div>
                        <div className="form-group">
                            <label htmlFor="productBrand">Marca</label>
                            <select id="productBrand" value={product.brand} onChange={handleInputChange}>
                                <option value="">Seleccione una marca</option>
                                {categories.map((category) => (
                                    <option key={category.id} value={category.id}>{category.categorieName}</option>
                                ))}
                            </select>
                        </div>
                        <div className="form-group">
                            <label htmlFor="productImage">Imagen del Producto</label>
                            <input type="file" id="productImage" onChange={handleFileChange} />
                        </div>

                        <button type="submit" className="form-button">Enviar</button>
                    </form>
                </div>
            </div>
        </LayoutFour>
    );
};

export default RegisterProduct;
