import React from 'react';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const registerCategory = () => {
    const [categories, setCategories] = useState([]);
    const [selectedCategory, setSelectedCategory] = useState({ id: '', categorieName: '' });
    const apiURL = "http://0.0.0.0:8090/api/collections/categories/records";

    const fetchCategories = async () => {
        try {
            const response = await axios.get(apiURL);
            setCategories(response.data.items);
        } catch (error) {
            console.error("Error al cargar las categorías:", error);
        }
    };

    useEffect(() => {
        fetchCategories();
    }, []);

    const handleSubmit = async (event) => {
        event.preventDefault();
        try {
            const data = { categorieName: selectedCategory.categorieName };
            if (selectedCategory.id) {
                await axios.put(`${apiURL}/${selectedCategory.id}`, data);
            } else {
                await axios.post(apiURL, data);
            }
            fetchCategories();
            setSelectedCategory({ id: '', categorieName: '' });
        } catch (error) {
            console.error("Error al guardar la categoría:", error);
        }
    };

    const handleUpdateClick = (category) => {
        setSelectedCategory(category);
    };

    const handleDeleteClick = async (categoryId) => {
        try {
            await axios.delete(`${apiURL}/${categoryId}`);
            fetchCategories();
        } catch (error) {
            console.error("Error al eliminar la categoría:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE CATEGORIAS" current />
            </Breadcrumb>

            <style>
                {`
                    .login-page {
                        width: 360px;
                        margin: auto;
                        padding-bottom: 60px;
                    }

                    .login-form input {
                        font-family: "Roboto", sans-serif;
                        outline: 0;
                        background: #f2f2f2;
                        width: 100%;
                        border: 0;
                        margin: 0 0 15px;
                        padding: 15px;
                        box-sizing: border-box;
                        font-size: 14px;
                    }

                    .login-form button {
                        font-family: "Roboto", sans-serif;
                        text-transform: uppercase;
                        outline: 0;
                        background: #f26460;
                        width: 100%;
                        border: 0;
                        padding: 15px;
                        color: #FFFFFF;
                        font-size: 14px;
                        -webkit-transition: all 0.3 ease;
                        transition: all 0.3 ease;
                        cursor: pointer;
                    }

                    .login-form button:hover,.login-form button:active,.login-form button:focus {
                        background: #ee3631;
                    }

                    .login-form label {
                        font-family: "Roboto", sans-serif;
                        font-size: 14px;
                        color: #333;
                        margin-bottom: 5px;
                        display: block;
                    }
                `}
            </style>

            <div className="login-page">
                <h2>Formulario de Categorias</h2>
                <div className="form">
                    <form className="login-form">
                        <label htmlFor="productName">Nombre de la Categoria</label>
                        <input type="text" id="productName" placeholder="Ingrese el nombre de la Categoria" />
                        <button type="submit" className="bold-text">Enviar</button>
                    </form>
                </div>
            </div>
        </LayoutFour>
    );
};

export default registerCategory;
