import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const Administration = () => {
    const [meetings, setMeetings] = useState([]);

    useEffect(() => {
        const fetchMeetings = async () => {
            try {
                const response = await axios.get('http://0.0.0.0:8090/api/collections/meetings/records');
                setMeetings(response.data.items);
            } catch (error) {
                console.error("Error al cargar reuniones:", error);
            }
        };

        fetchMeetings();
    }, []);

    const handleDelete = async (meetingId) => {
        try {
            await axios.delete(`http://0.0.0.0:8090/api/collections/meetings/records/${meetingId}`);
            setMeetings(meetings.filter(meeting => meeting.id !== meetingId));
        } catch (error) {
            console.error("Error al eliminar reunión:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACIÓN" />
                <BreadcrumbItem name="GESTIÓN DE CITAS" current />
            </Breadcrumb>

            <div className="meetings-report">


                <table className="data-table">
                    <thead>
                        <tr>
                            <th>Nombre</th>
                            <th>Teléfono</th>
                            <th>Servicio</th>
                            <th>Acciones</th>
                        </tr>
                    </thead>
                    <tbody>
                        {meetings.map((meeting) => (
                            <tr key={meeting.id}>
                                <td>{meeting.name}</td>
                                <td>{meeting.phone}</td>
                                <td>{meeting.service}</td>
                                <td>
                                    <button onClick={() => handleDelete(meeting.id)}>Eliminar</button>
                                </td>
                            </tr>
                        ))}
                    </tbody>
                </table>
            </div>
        </LayoutFour>
    );
};

export default Administration;
