import React, { useState, useEffect } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterProduct = () => {
    const [categories, setCategories] = useState([]);
    const [products, setProducts] = useState([]);
    const [product, setProduct] = useState({
        id: '',
        name: '',
        price: '',
        brand: '',
        categorieName: '',
        thumbImage: null,
        images: [],
        rate: '',
        new: '',
        code: '',
        point: '',
        quantity: '',
        description: ''
    });

    const categoriesApiUrl = "http://0.0.0.0:8090/api/collections/categories/records";
    const productsApiUrl = "http://0.0.0.0:8090/api/collections/products/records";

    useEffect(() => {
        const fetchData = async () => {
            try {
                const [categoriesResponse, productsResponse] = await Promise.all([
                    axios.get(categoriesApiUrl),
                    axios.get(productsApiUrl)
                ]);
                const categoriesMap = categoriesResponse.data.items.reduce((acc, category) => {
                    acc[category.id] = category.categorieName;
                    return acc;
                }, {});
                setCategories(categoriesResponse.data.items); // Arreglo de categorías
                setCategoriesMap(categoriesMap); // Objeto para mapeo de ID a nombre
                setProducts(productsResponse.data.items);
            } catch (error) {
                console.error("Error al cargar datos:", error);
            }
        };
        fetchData();
    }, []);

    const handleInputChange = (e) => {
        const { id, value } = e.target;
        setProduct(prevState => ({
            ...prevState,
            [id]: value
        }));
    };

    const handleFileChange = (e, type) => {
        if (type === 'thumbImage') {
            setProduct({ ...product, thumbImage: e.target.files[0] });
        } else if (type === 'images') {
            setProduct({ ...product, images: [...e.target.files] });
        }
    };

    const handleSubmit = async (event) => {
        event.preventDefault();
        const formData = new FormData();
        formData.append('name', product.name);
        formData.append('price', product.price);
        formData.append('brand', product.brand);
        formData.append('categorieName', product.categorieName);
        formData.append('rate', product.rate);
        formData.append('new', product.new);
        formData.append('code', product.code);
        formData.append('point', product.point);
        formData.append('quantity', product.quantity);
        formData.append('description', product.description);
        if (product.thumbImage) {
            formData.append('thumbImage', product.thumbImage);
        }
        if (product.images && product.images.length) {
            product.images.forEach((file, index) => {
                formData.append(`images_${index}`, file);
            });
        }

        try {
            if (product.id) {
                await axios.patch(`${productsApiUrl}/${product.id}`, formData);
            } else {
                await axios.post(productsApiUrl, formData);
            }
            fetchData();
            setProduct({ id: '', name: '', price: '', brand: '', categorieName: '', thumbImage: null, images: [], rate: '', new: '', code: '', point: '', quantity: '', description: '' });
        } catch (error) {
            console.error("Error al procesar el producto:", error);
        }
    };

    const handleUpdateClick = (productToUpdate) => {
        // Asegúrate de que la categoría se maneje correctamente aquí
        setProduct({ ...productToUpdate, categorieName: categories[productToUpdate.categorieName] });
    };

    const handleDeleteClick = async (productId) => {
        try {
            await axios.delete(`${productsApiUrl}/${productId}`);
            fetchData();
        } catch (error) {
            console.error("Error al eliminar el producto:", error);
        }
    };

    return (
        <LayoutFour title="ADMINISTRACIÓN" className="-style-1">
            <Breadcrumb title="ADMINISTRACIÓN">
                <BreadcrumbItem name="ADMINISTRACION" />
                <BreadcrumbItem name="REGISTRO DE PRODUCTOS" current />
            </Breadcrumb>

            <style>
                {`
                @import url('https://fonts.googleapis.com/css?family=Roboto:300');
                .form-container {
                    width: 360px;
                    padding: 20px;
                    margin: 40px auto;
                    background-color: #f9f9f9;
                    border-radius: 8px;
                    box-shadow: 0 0 10px rgba(0,0,0,0.1);
                    transition: all 0.3s ease;
                }

                .form-input, .form-select, .form-control-file {
                    font-family: "Roboto", sans-serif;
                    outline: 0;
                    background: #f2f2f2;
                    width: 100%;
                    border: 0;
                    margin: 0 0 15px;
                    padding: 15px;
                    box-sizing: border-box;
                    font-size: 14px;
                }

                .form-button {
                    font-family: "Roboto", sans-serif;
                    text-transform: uppercase;
                    outline: 0;
                    background: #f26460;
                    width: 100%;
                    border: 0;
                    padding: 15px;
                    color: #FFFFFF;
                    font-size: 14px;
                    cursor: pointer;
                    transition: all 0.3s ease;
                    font-weight: bold;
                }

                .form-button:hover {
                    background: #ee3631;
                }

                /* Estilos para la Tabla */
                .data-table {
                    width: 100%;
                    margin-top: 20px;
                    border-collapse: collapse;
                }

                .data-table th, .data-table td {
                    text-align: left;
                    padding: 8px;
                    border-bottom: 1px solid #ddd;
                }

                .data-table th {
                    background-color: #f26460;
                    color: white;
                }

                .data-table tbody tr:nth-child(odd) {
                    background-color: #f9f9f9;
                }

                .table-button {
                    background: #f26460;
                    color: #FFFFFF;
                    border: none;
                    padding: 5px 10px;
                    border-radius: 5px;
                    cursor: pointer;
                    margin-right: 5px;
                }

                .table-button:hover {
                    background: #ee3631;
                }

                .containerAll {
                    padding-bottom: 70px;
                }
            `}
            </style>

            <div className="containerAll">
            <div className="form-container">
                <div className="form-group">
                    <label htmlFor="productName">Nombre del Producto</label>
                    <input
                        type="text"
                        className="form-input"
                        id="name"
                        placeholder="Ingrese el nombre del producto"
                        value={product.name}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="productPrice">Precio</label>
                    <input
                        type="number"
                        className="form-input"
                        id="price"
                        placeholder="Ingrese el precio"
                        value={product.price}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="productBrand">Marca</label>
                    <input
                        type="text"
                        className="form-input"
                        id="brand"
                        placeholder="Ingrese la marca"
                        value={product.brand}
                        onChange={handleInputChange}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="categorieName">Categoría</label>
                    <select className="form-input" id="categorieName" value={product.categorieName} onChange={handleInputChange}>
    <option value="">Seleccione una categoría</option>
    {categories.map(category => (
        <option key={category.id} value={category.id}>{category.categorieName}</option>
    ))}
</select>
                </div>
                <div className="form-group">
                    <label htmlFor="thumbImage">Imagen Miniatura</label>
                    <input
                        type="file"
                        className="form-input"
                        id="thumbImage"
                        onChange={(e) => handleFileChange(e, 'thumbImage')}
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="images">Imágenes</label>
                    <input
                        type="file"
                        className="form-input"
                        id="images"
                        multiple
                        onChange={(e) => handleFileChange(e, 'images')}
                    />
                    <div className="form-group">
                        <label htmlFor="productRate">Rate</label>
                        <input type="number" className="form-input" id="rate" placeholder="Rate" value={product.rate} onChange={handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productStatus">Estado</label>
                        <select className="form-input" id="new" value={product.new} onChange={handleInputChange}>
                            <option value="true">True</option>
                            <option value="false">False</option>
                        </select>
                    </div>
                    <div className="form-group">
                        <label htmlFor="productCode">Código</label>
                        <input type="text" className="form-input" id="code" placeholder="Código" value={product.code} onChange={handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productPoint">Puntos</label>
                        <input type="number" className="form-input" id="point" placeholder="Puntos" value={product.point} onChange={handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productQuantity">Cantidad</label>
                        <input type="number" className="form-input" id="quantity" placeholder="Cantidad" value={product.quantity} onChange={handleInputChange} />
                    </div>
                    <div className="form-group">
                        <label htmlFor="productDescription">Descripción</label>
                        <textarea className="form-input" id="description" placeholder="Descripción" value={product.description} onChange={handleInputChange}></textarea>
                    </div>
                </div>
                <button type="submit" className="form-button">Enviar</button>
            </div>
            {/* Tabla de Productos */}
            <table className="data-table">
                <thead>
                    <tr>
                        <th>Nombre</th>
                        <th>Precio</th>
                        <th>Marca</th>
                        <th>Categoría</th>
                        <th>Acciones</th>
                    </tr>
                </thead>
                <tbody>
                    {products.map((product) => (
                        <tr key={product.id}>
                            <td>{product.name}</td>
                            <td>{product.price}</td>
                            <td>{product.brand}</td>
                            <td>{categories[product.categorieName]}</td>                            
                            <td>
                                <button className="table-button" onClick={() => handleUpdateClick(product)}>Actualizar</button>
                                <button className="table-button" onClick={() => handleDeleteClick(product.id)}>Eliminar</button>
                            </td>
                        </tr>
                    ))}
                </tbody>
            </table>
            </div>
        </LayoutFour>
    );
};

export default RegisterProduct;
