import React, { useEffect } from 'react';

function LoginPage() {
  // Función para manejar el evento click
  const handleToggleForm = () => {
    const forms = document.querySelectorAll('form');
    forms.forEach((form) => {
      const formStyle = form.style;
      const currentHeight = formStyle.height || 'auto';
      const currentOpacity = formStyle.opacity || '1';

      formStyle.height = currentHeight === '0px' ? 'auto' : '0px';
      formStyle.opacity = currentOpacity === '0' ? '1' : '0';
    });
  };

  // Agregar el evento click al elemento
  useEffect(() => {
    const messageLink = document.querySelector('.message a');
    if (messageLink) {
      messageLink.addEventListener('click', handleToggleForm);
    }

    // Retirar el evento cuando el componente se desmonte
    return () => {
      if (messageLink) {
        messageLink.removeEventListener('click', handleToggleForm);
      }
    };
  }, []);

  return (
    <div className="login-page">
      <div className="form">
        <form className="register-form">
          <input type="text" placeholder="name" />
          <input type="password" placeholder="password" />
          <input type="text" placeholder="email address" />
          <button>create</button>
          <p className="message">Already registered? <a href="#">Sign In</a></p>
        </form>
        <form className="login-form">
          <input type="text" placeholder="username" />
          <input type="password" placeholder="password" />
          <button>login</button>
          <p className="message">Not registered? <a href="#">Create an account</a></p>
        </form>
      </div>
    </div>
  );
}

export default LoginPage;
