import React, { useState } from 'react';
import axios from 'axios';
import LayoutFour from "../../components/Layout/LayoutFour";
import { Breadcrumb, BreadcrumbItem } from "../../components/Other/Breadcrumb";

const RegisterCategory = () => {
    const [service, setService] = useState({
        serviceName: '',
        videoSrc: '',
        description: '',
        serviceImage: null
    });

    const handleInputChange = (e) => {
        setService({ ...service, [e.target.id]: e.target.value });
    };

    const handleFileChange = (e) => {
        setService({ ...service, serviceImage: e.target.files[0] });
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        const formData = new FormData();
        formData.append('serviceName', service.serviceName);
        formData.append('videoSrc', service.videoSrc);
        formData.append('description', service.description);

        if (service.serviceImage) {
            formData.append('serviceImage', service.serviceImage);
        }

        try {
            const response = await axios.post('http://127.0.0.1:8090/api/your-service-endpoint', formData);
            console.log('Service submitted:', response.data);
            // Limpiar el formulario o redirigir según necesidad
        } catch (error) {
            console.error("Error al enviar el servicio:", error);
        }
    };
};
