import { useForm } from "react-hook-form";

import SectionTitleOne from "../SectionTitle/SectionTitleOne";

const [bannerImage, setBannerImage] = useState('');
useEffect(() => {
  async function fetchBannerImage() {
    try {
      const response = await fetch('http://localhost:8090/api/collections/banner/records');
      const data = await response.json();
      if (data.items && data.items.length > 0) {
        const imageUrl = `http://localhost:8090/api/files/${data.items[0].collectionId}/${data.items[0].id}/${data.items[0].image_1920x1080}`;
        setBannerImage(imageUrl);
      }
    } catch (error) {
      console.error('Error fetching banner data:', error);
    }
  }

  fetchBannerImage();
}, []);

export default function CTAOne() {
  const { register, handleSubmit, watch, errors } = useForm({
    mode: "onChange",
  });
  const onSubmit = (data) => {
    console.log(data);
  };
  return (
    <div
      className="cta -style-1"
      style={{ backgroundImage: `url("${bannerImage}")` }}
    >
      <div className="container">
        <div className="row">
          <div className="col-12 col-md-6 mx-auto">
            <div className="cta__form">
              <SectionTitleOne align="center" spaceBottom="1.875em">
                AGENDA UNA CITA
              </SectionTitleOne>
              <form
                onSubmit={handleSubmit(onSubmit)}
                className="cta__form__detail"
              >
                <div className="input-validator">
                  <input
                    type="text"
                    placeholder="NOMBRE"
                    name="name"
                    ref={register({ required: true })}
                  />
                  {errors.name && (
                    <span className="input-error">
                      Por favor, proporcione un nombre válido.
                    </span>
                  )}
                </div>
                <div className="input-validator">
                  <input
                    type="text"
                    placeholder="TELÉFONO"
                    name="phone"
                    ref={register({ required: true })}
                  />
                  {errors.phone && (
                    <span className="input-error">
                      Por favor, proporcione un teléfono válido.
                    </span>
                  )}
                </div>
                <div className="input-validator">
                  <select
                    name="service"
                    ref={register({ required: true })}
                    className="customed-select"
                    defaultValue=""
                  >
                    <option value="" hidden>
                      ELEGIR UN SERVICIO
                    </option>
                    {["SERVICIO1", "SERVICIO2", "SERVICIO3"].map((item, index) => (
                      <option key={index} value={item}>
                        {item}
                      </option>
                    ))}
                  </select>
                  {errors.service && (
                    <span className="input-error">Por favor, elija un servicio.</span>
                  )}
                </div>
                <button className="btn -light-red">AGENDAR</button>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
