import Slider from "react-slick";

import Button from "../../Control/Button";
import { PrevArrow, NextArrow } from "../../Other/SliderArrow";

export default function IntroductionNine() {
  const [images, setImages] = useState([]);

  useEffect(() => {
    async function fetchImages() {
      try {
        const response = await fetch('http://localhost:8090/api/collections/introductionNineImages/records');
        const data = await response.json();
        const formattedData = data.items.map(item => ({
          src: `http://localhost:8090/api/files/${item.collectionId}/${item.id}/${item.imageFile}`,
          alt: item.altText || 'Brand item'
        }));
        setImages(formattedData);
      } catch (error) {
        console.error('Error fetching images:', error);
      }
    }

    fetchImages();
  }, []);
  
  const settings = {
    dots: false,
    infinite: false,
    speed: 500,
    slidesToShow: 6,
    slidesToScroll: 1,
    prevArrow: <PrevArrow />,
    nextArrow: <NextArrow />,
    responsive: [
      {
        breakpoint: 1200,
        settings: {
          slidesToShow: 5,
        },
      },
      {
        breakpoint: 992,
        settings: {
          slidesToShow: 3,
        },
      },
      {
        breakpoint: 576,
        settings: {
          slidesToShow: 2,
        },
      },
    ],
  };
  return (
    <div className="introduction-nine">
      <div className="introduction-nine__logos">
        <div className="container">
          <Slider {...settings}>
            {Array.from(Array(7), (e, i) => {
              return (
                <div key={i} className="slide__item">
                  <img
                    src={
                      process.env.PUBLIC_URL +
                      `/assets/images/introduction/IntroductionNine/${
                        i + 1
                      }.png`
                    }
                    alt="Brand item"
                  />
                </div>
              );
            })}
          </Slider>
        </div>
      </div>
      <div className="container">
        <div className="introduction-nine__content">
          <h3>
            ¡NUEVOS ÍTEMS SON <br />
            LANZADOS SEMANALMENTE!
          </h3>
          <Button action="/shop/fullwidth-4col" color="white" content="NUEVOS ÍTEMS" />
        </div>
      </div>
    </div>
  );
}
